            dialog.render();
            dialog.open();
        };
    });
    // 多图上传动作
    function _beforeInsertImage(t, result) {
        var imageHtml = '';
        var ids = [];
        for (var i in result) {
            ids.push(result[i].src);
            imageHtml += '<li style=""float: left;""><img  src=""' + result[i].src + '"" alt=""' + result[i].alt + '"" height=""150"" onclick=""previewImg(this)""></li>';
        }
        $(""#F_ImageUrl"").val(ids.join(','));
        document.getElementById('upload_img_wrap').innerHTML = imageHtml;
    }
    function previewImg(obj) {
